                        <script>
<?php
if(isset($_GET['item_id'])) {
    $id = $_GET['item_id'];
    $ratedCounts = array_fill(1, 5, 0); 
    $selectAllRatingCondition = "SELECT * FROM irate_ratings WHERE item_id = $id";
    $check = mysqli_query($connforMyOnlineDb, $selectAllRatingCondition);
    while ($test = mysqli_fetch_assoc($check)) {
        $rating = $test['rate'];
        $ratedCounts[$rating]++;
    }
?>
var ctx = document.getElementById('ratingsChart').getContext('2d');
var ratingsChart = new Chart(ctx, {
    type: 'bar',
    data: {
        labels: ['1', '2', '3', '4', '5'],
        datasets: [{
            label: 'Number of Users',
            data: <?php echo json_encode(array_values($ratedCounts)); ?>,
            backgroundColor: [
                'rgba(255, 99, 132, 0.2)',
                'rgba(54, 162, 235, 0.2)',
                'rgba(255, 206, 86, 0.2)',
                'rgba(75, 192, 192, 0.2)',
                'rgba(153, 102, 255, 0.2)',
            ],
            borderColor: [
                'rgba(255, 99, 132, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(75, 192, 192, 1)',
                'rgba(153, 102, 255, 1)',
            ],
            borderWidth: 1
        }]
    },
    options: {
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero: true,
                    stepSize: 1 // Specify step size as 1
                }
            }]
        }
    }
});
<?php } ?>
</script>
